"use strict";function pickCase(){var t=Math.floor(100*Math.random());return t>=60?"up":"lo"}var text=document.body.querySelector("#text");text.onkeydown=function(){var t=text.value.charAt(text.value.length-1);if("up"===pickCase()){var e=t.toUpperCase();text.value=text.value.substr(0,text.value.length-1)+e}else{var a=t.toLowerCase();text.value=text.value.substr(0,text.value.length-1)+a}};